import React, { Component } from 'react'

class Sidebar extends Component {

    constructor(props) {
        super(props)

        this.state = {
           hidden: true
        }
    }

    hideShow() {
        this.setState({
            hidden: false
        })
    }

    render() {
        return (
            <div>
               <button onclick={() => this.hideShow()}>Hide/Show</button> 
            </div>
        )
    }
}

export default Sidebar
