import React, { Component } from 'react'
import './Sidebar.css'

class Sidebar extends Component {

    constructor(props) {
        super(props)

        this.state = {
             css: ''
        }
    }


    toggleNav() {

        var state = () => {
            hidden ^= false
        }

        this.setState({
            css: state
        })


    }

    render() {
        return (
            <div>
               <button onclick={this.toggleSidenav.bind(this)}>Hide/Show</button>
               <div className={this.props.sidenav}>
                   <p>test</p>
               </div>
            </div>
        )
    }
}

export default Sidebar
