import React, { Component } from 'react'
import './Sidebar.css'

class Sidebar extends Component {

    constructor(props) {
        super(props)

        this.state = {
             css: ""
        }
    }


    toggleNav() {
        var state = () => {
            if (this.state === "hidden")
            {
                state = "show"
            }
            else {
                state = "hidden"
            }
        }

        this.setState({
            css: state()
        })

    }

    render() {
        return (
            <div>
               <button onclick={this.toggleSidenav.bind(this)}>Hide/Show</button>
               <div className={this.props.state.css}>
                   <p>test</p>
               </div>
            </div>
        )
    }
}

export default Sidebar
