import React, { Component } from 'react'
import './Sidebar.css'

class Sidebar extends Component {

    constructor(props) {
        super(props)

        this.state = {
             css: ''
        }
    }


    toggleSidenav() {
        var css = (this.props.sidenav === "hidden") ? "show" : "hidden";
        this.setState({"sidenav":css});
    }

    render() {
        return (
            <div>
               <button onclick={this.toggleSidenav.bind(this)}>Hide/Show</button>
               <div className={this.props.sidenav}>
                   <p>test</p>
               </div>
            </div>
        )
    }
}

export default Sidebar
