import React, { Component } from 'react'

class Sidebar extends Component {


    //function to toggle
    getState(){
        return {"sideMenu":"hidden"}
    }

    toggleSidenav() {
        var css = (this.props.sidenav === "hidden") ? "show" : "hidden";
        this.setState({"sidenave":css});
    }

    render() {
        return (
            <div>
               <button onclick={() => this.hideShow()}>Hide/Show</button>
               <div className={this.props.sidenav} >
                   <p>test</p>
               </div>
            </div>
        )
    }
}

export default Sidebar
