import React, {useState} from 'react';
import Pasta from './Pasta';
import Pizzas from './Pizzas'
import Sidebar from '../Sidebar/Sidebar';

function Menu ({ pizzaData, pastaData, section }) {

  const [active, setActive] = useState("Pizzas")
  const text = <Sidebar section={section}/>
  return (
      <div>
        {active === "Pizzas" && <Pizzas pizzaCard={pizzaData}/>}
        {active === "Pastas" && <Pasta pastaCard={pastaData}/>}
    </div>
  );
  console.log(text)
}

export default Menu
