import React from 'react'
import buttercrust from '../Assets/butter.png'
import './Sidebar.css'
import Items from './Items'

function Sidebar() {

    const expansion = () => {
        for (let i = 0; i < expansion.length; i++) {
            expansion[i].addEventListener("click", function() {
              /* Toggle between adding and removing the "active" class,
              to highlight the button that controls the panel */
              this.classList.toggle("active");

              /* Toggle between hiding and showing the active panel */
              var panel = this.nextElementSibling;
              if (panel.style.display === "block") {
                panel.style.display = "none";
              } else {
                panel.style.display = "block";
              }
            });
          }
    }

    return (
        <div className="sidebar">
             <div className="logo-div">
                 <img className="logo" src={buttercrust} alt="headlogo"/>
             </div>
            <div>
                <button className="accordion">Crust</button>
                <div className="panel">
                <Items/>
                </div>
            </div>
            <div>
                <button className="btn-sidemenu">Done</button>
            </div>
        </div>
    )
}

export default Sidebar
